const initializeProductPrimarySlider=e=>{var r=parseInt(e.getAttribute("data-number-slides"));new Swiper(e,{slidesPerView:r,spaceBetween:30,pagination:{el:".products-primary__pagination",type:"bullets",clickable:!0},breakpoints:{280:{slidesPerView:1,spaceBetween:10},480:{slidesPerView:r,spaceBetween:30}}})},productsPrimary=()=>{[...document.querySelectorAll(".products-primary")].map(e=>{e=e.querySelector(".products-primary__slider");initializeProductPrimarySlider(e)})};document.addEventListener("DOMContentLoaded",function(){productsPrimary()});